import React, { useState } from 'react';
import { FiEye, FiEyeOff } from 'react-icons/fi';
import Header from '../../Main/components/Header';
import '../styles/Login.css';
import '../styles/ClinicLogin.css';

function ClinicLogin() {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:3001/login/clinica?email=${email}&senha=${password}`);

      if (response.ok) {
        const data = await response.json();
        if (data.id) {
          console.log('Usuário logado com sucesso. ID:', data.id);
        } else {
          setMessage('Usuário não encontrado no banco de dados.');
        }
      } else {
        setMessage('Credenciais inválidas. Verifique seu email e senha.');
      }
    } catch (error) {
      console.error('Erro na solicitação GET:', error);
      setMessage('Ocorreu um erro ao fazer login. Tente novamente mais tarde.');
    }
  };

  return (
    <div>
      <Header />
      <div className="login_clinic">
        <div className="login_form">
          <p className="login_title">Entre na conta da sua Clínica:</p>
          <form onSubmit={handleSubmit}>
            <div className="input_container">
              <label>E-mail:</label>
              <input
                type="text"
                placeholder='Digite seu e-mail'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="input_container">
              <div className="pass_forgot">
                <label>Senha:</label>
                <a href='/forgot' className='forgot_a'>Esqueceu?</a>
              </div>
              <div className="password-input">
                <input
                  type={showPassword ? 'text' : 'password'}
                  placeholder="Digite sua senha"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <p onClick={handleShowPassword} className="eye-icon">
                  {showPassword ? <FiEyeOff /> : <FiEye />}
                </p>
              </div>
            </div>
            <div className="button_login_clinic">
              <button type="submit">Entrar</button>
            </div>
            <div className="register_page">
              <span>Ainda não tem uma conta? <a href="/registerClinic" className='cad'>Cadastre-se</a></span>
            </div>
          </form>
          <p className="login_message">{message}</p>
        </div>
      </div>
    </div>
  );
}

export default ClinicLogin;
