import React, { useState } from 'react';
import { FaBars } from 'react-icons/fa';



function Header(){

        const [menuActive, setMenuActive] = useState(false);

    const toggleMenu = () => {
        setMenuActive(!menuActive);
    };

    return (
        <header className={`header ${menuActive ? 'menu-active' : ''}`}>

            <a href="/" className="logo"> Bebê-Vindo </a>

            <div id="menu-btn" onClick={toggleMenu}> <FaBars /> </div>

            <nav className={`navbar ${menuActive ? 'menu-active' : ''}`}>
                <a href="/">Início</a>
                <a href="/about">Sobre</a>
                <a href="/services">Serviços</a>
                <a href="/blog">Blog</a>
                <a id='login-btn' href="/login">Login</a>
            </nav>
        </header>
    );
};

export default Header;
