import axios from 'axios'; // ou a biblioteca que você está usando

import React, { useState } from 'react'; // Importe o useState para gerenciar o estado do componente
import Header from '../../Main/components/Header';

import '../styles/RegisterClinic.css';

function RegisterClinicContinue() {
  const [descricaoClinica, setDescricaoClinica] = useState(''); // Use o useState para gerenciar o estado do textarea

  const cadastrarClinica = async () => {
    try {
      // Use o estado (descricaoClinica) em vez de document.getElementById
      const response = await axios.post('http://localhost:3000/clinica', {
        descricao: descricaoClinica, // Passe a descrição para o servidor
        // Outros campos do formulário podem ser adicionados aqui
      });

      console.log('Cadastro da clínica realizado com sucesso!', response.data);
      // Lógica adicional após o cadastro, como redirecionar o usuário, pode ser adicionada aqui.
    } catch (error) {
      console.error('Erro ao cadastrar a clínica:', error);
    }
  };

  return (
    <div className="register">
      <Header />
      <div className="register_ii">
        <h1 className='registre'>Cadastre sua Clínica</h1>
        <div className="image-upload-container">
          <input type="file" id="fileInput" accept="image/*"></input>
          <label htmlFor="fileInput" className="add-button">+</label>
        </div>
        <div className="clinic_description">
          <p className="registration_information">Descrição: *</p>
          <textarea
            id='description_clinic'
            value={descricaoClinica} // Use o valor do estado para controlar o textarea
            onChange={(e) => setDescricaoClinica(e.target.value)} // Atualize o estado quando o textarea for alterado
          ></textarea>
        </div>
        <div className="register_button">
          {/* Chame a função cadastrarClinica quando o botão for clicado */}
          <button className='continue_register' onClick={cadastrarClinica}>Cadastrar</button>
        </div>
      </div>
    </div>
  );
}

export default RegisterClinicContinue;
