import React, { useState, useEffect } from 'react';
import { FiEye, FiEyeOff } from 'react-icons/fi';
import Header from '../../Main/components/Header';
import '../styles/Login.css';
import '../styles/ClinicLogin.css';

function ClinicLogin() {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/clinica', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserData(data);
      } else {
        setError('Credenciais inválidas. Verifique seu email e senha.');
      }
    } catch (error) {
      console.error('Erro na solicitação POST:', error);
      setError('Ocorreu um erro ao fazer login. Tente novamente mais tarde.');
    }
  };

  return (
    <div>
      <Header />
      <div className="login_clinic">
        <p className="login_title">Entre na conta da sua Clínica:</p>
        <form onSubmit={handleSubmit}>
          <div className="input_container">
            <label>E-mail:</label>
            <input
              type="text"
              placeholder='Digite seu e-mail'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="input_container">
            <div className="pass_forgot">
              <label>Senha:</label>
              <a href='/forgot' className='forgot_a'>Esqueceu?</a>
            </div>
            <div className="password-input">
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Digite sua senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <p onClick={handleShowPassword} className="eye-icon">
                {showPassword ? <FiEyeOff /> : <FiEye />}
              </p>
            </div>
          </div>
          <div className="button_login_clinic">
            <button type="submit">Entrar</button>
          </div>
          {error && <p className="error-message">{error}</p>}
        </form>
      </div>
      {/* Exibir os dados do usuário se o login for bem-sucedido */}
      {userData && (
        <div className="user-data">
          <p>Dados do usuário:</p>
          <pre>{JSON.stringify(userData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default ClinicLogin;
